var fs = require('fs');
var os = require('os');

module.exports.load = function()
{
    console.log("config.js - Loading configuration...");
    loadApp();
    loadDatabase();
    loadSession();
    console.log("config.js - Loaded configuration.")
};

var loadApp = function()
{
    module.exports.app = JSON.parse(fs.readFileSync("app.cfg"));
};

var loadDatabase = function() {
    module.exports.mysql = JSON.parse(fs.readFileSync("mysql.cfg"));
};

var loadSession = function() {
    module.exports.session = JSON.parse(fs.readFileSync("session.cfg"));
};